'use strict';

/** @ignore */
let EmbedValidationError = require('./errors/EmbedValidationError');

/**
 * Embed data object that holds embedOptions and resulting data.
 */
class Embed {
  /**
  * @param {String} embedURL - URL to be embedded
  * @param {Object} embedOptions - Additional options
  * @throws {EmbedValidationError} - if embedURL is undefined.
  */
  constructor (embedURL, embedOptions) {
    if (!embedURL) {
      throw new EmbedValidationError('embedURL is undefined');
    }
    /**
    * The URL to be embedded
    * @type {String}
    */
    this.embedURL = embedURL;

    /**
    * Will be populated with an Error object if an error occurs while processing this embed
    * @type {Error}
    */
    this.error = null;

    /**
    * Options for this Embed
    * @type {Object}
    */
    this.options = embedOptions || {};

    /**
    * Default markup for unprocessed embed.data.html
    * @type {String}
    */
    this.stubMarkup = '<!-- default embed markup -->';

    /**
    * Data object for the resolved embed
    *
    * __Note:__ there should always be a populated data.html property, even if the embed encounters an error while resolving.
    * @type {{html: String}}
    */
    this.data = {
      html: this.stubMarkup
    };

    /**
    * Time the embed began resolving
    * @type {Date}
    */
    this.startedDate = null;

    /**
    * Time the embed finished resolving
    * @type {Date}
    */
    this.finishedDate = null;

    /**
    * Milliseconds elapsed during resolving embed
    * @type {number}
    */
    this.elapsedMs = null;

    /**
    * The provider API URL that was used to resolve the embed
    * @type {String}
    */
    this.oembedAPIURL = null;
  }

  /**
  * Timestamps the beginning of the embed resolve process.
  */
  markStarted () {
    this.startedDate = new Date();
  }

  /**
  * Timestamps the end of the embed resolve process and calculates elapsedMs
  */
  markFinished () {
    this.finishedDate = new Date();
    this.elapsedMs = this.finishedDate.getTime() - this.startedDate.getTime();
  }
}

module.exports = Embed;

'use strict';

/** @ignore */
let async = require('async');

/** @ignore */
let URLEmbedProvider = require('./URLEmbedProvider');

/** @ignore */
let OEmbedProvider = require('./OEmbedProvider');

/** @ignore */
let utils = require('../utils');

/** @ignore */
let UnknownProviderError = require('./errors/UnknownProviderError');

/**
 * Resolves urls to embeds from a registered list of embed providers
 */
class EmbedEngine {
  /**
  * @param {Object} [engineOptions] - configuration options
  * @param {number} [engineOptions.timeoutMs] - request timeout in milliseconds
  */
  constructor (engineOptions) {
    /**
    * Utility references to provider superclasses
    * @type {{URLEmbedProvider: URLEmbedProvider, OEmbedProvider: OEmbedProvider}}
    */
    this.providerSuperClasses = {
      URLEmbedProvider: URLEmbedProvider,
      OEmbedProvider: OEmbedProvider
    };

    /**
    * The directory containing the default provider classes
    * @type {String}
    */
    this.defaultProviderDirectory = __dirname + '/default_providers';

    /**
    * The registry of provider instances
    * @type {Array<URLEmbedProvider>}
    */
    this.providerRegistry = [];

    /**
     * Array of provider classes included in this library
     * @type {Array<URLEmbedProvider>} defaultProviderClasses - array of provider classes included in this library
     */
    this.defaultProviderClasses = utils.loadClassesFromDirectory(this.defaultProviderDirectory);

    /**
    * Configuration options. These will also be passed to the configure method of each provider instance
    * @type {Object} engineOptions - configuration options
    * @property {number} engineOptions.timeoutMs - request timeout in milliseconds
    */
    this.engineOptions = engineOptions;
  }

  /**
  * Registers a provider instance.
  *
  * __Highlander Rule__: this provider will replace an already registered provider if they have the same name property.
  *
  * @param {URLProvider} provider - instance of <code>URLProvider</code> or <code>OEmbedProvider</code>
  */
  registerProvider (provider) {
    let providerExists = false;

    this._configureProvider(provider);

    for (let idx = 0; idx < this.providerRegistry.length; idx++) {
      if (this.providerRegistry[idx].name === provider.name) {
        this.providerRegistry[idx] = provider;
        providerExists = true;
      }
    }

    if (!providerExists) {
      this.providerRegistry.push(provider);
    }
  }

  _configureProvider (provider) {
    provider.configure(this.engineOptions);
  }

  /**
  * Registers all the default providers in this library
  * @param {String} [path] - directory of provider classes (if not specified it will default to the ones in this library)
  */
  registerDefaultProviders (path) {
    for (let i = 0; i < this.defaultProviderClasses.length; i++) {
      let ProviderClass = this.defaultProviderClasses[i];
      let provider = new ProviderClass();
      this._configureProvider(provider);
      this.registerProvider(provider);
    }
  }

  /**
  * Resolves an Embed object, populating its data property and calling the callback
  * @param {Embed} embed - Embed object
  * @param {function(embed: Embed)} callback - callback to invoke after resolving Embed
  */
  getEmbed (embed, callback) {
    embed.markStarted();

    let providerFound = false;

    for (let idx = 0; idx < this.providerRegistry.length; idx++) {
      let provider = this.providerRegistry[idx];
      let self = this;

      if (provider.isMatch(embed.embedURL)) {
        providerFound = true;

        provider.getEmbed(embed, function (embed) {
          self.filterData(embed.data);
          embed.markFinished();
          callback(embed);
          return;
        });

        idx = this.providerRegistry.length;
      }
    }

    if (!providerFound) {
      embed.error = new UnknownProviderError('Unknown embed provider for url: ' + embed.embedURL);
      embed.data = {
        html: this.errorMarkupNoMatchingProvider(embed)
      };
      embed.markFinished();
      callback(embed);
    }
  }

  /*
  * Resolves an array of embedOptions.embedURL's in parallel
  * @param {Array} optArray - Array of embed options objects
  * @param {function(error: Error, results: Array)} callback - callback to invoke after batch is complete
  */
  getMultipleEmbeds (optArray, callback) {
    let self = this;

    async.map(optArray, function (options, callbackIterator) {
      self.getEmbed(options, function (embed) {
        callbackIterator(null, embed);
      });
    },
    function (error, results) {
      callback(error, results);
    });
  }

  /**
  * Returns error markup if there was no provider that matched the embedURL
  * @param {Embed} embed - Embed object
  */
  errorMarkupNoMatchingProvider (embed) {
    return '<a href="' + embed.embedURL + '">' + embed.embedURL + '</a>';
  }

  /**
  * Can be overridden/replaced to modify any provider's data before it is passed to the client callback.<br />
  * *Note:* if you only need to modify a specific provider's data then there's a similar <code>filterData</code> method on each provider object, too.
  * @param {{html: string}} data - Data object containing embed html
  */
  filterData (data) {}
}

module.exports = EmbedEngine;

'use strict';

/** @ignore */
let querystring = require('querystring');

/** @ignore */
let request = require('request');

/** @ignore */
let fs = require('fs');

/** @ignore */
let UnexpectedStatusError = require('./errors/UnexpectedStatusError');

/** @ignore */
let URLEmbedProvider = require('./URLEmbedProvider');

/** @ignore */
let xml2js = require('xml2js');

/**
* Converts a pattern of URLs into markup via an oembed provider
* @see http://oembed.com
* @extends {URLEmbedProvider}
* @interface
*/
class OEmbedProvider extends URLEmbedProvider {

  /**
  * @param {String} providerURL - the URL of the provider's oembed service
  * @param {Array<RegExp>} urlPatterns - array of regular expressions that this provider will match
  * @param {String} format - the data format of the provider's oembed service. Must be either 'json' or 'xml'.
  */
  constructor (providerURL, urlPatterns, format) {
    super(urlPatterns);

    /**
    * The URL of the provider's oembed service
    * @type {String}
    */
    if (providerURL) this.providerURL = providerURL;

    /**
    * @desc The data format of the provider's oembed service.
    * @type {String}
    */
    if (format) this.format = format;

    /**
    * The version number of the url-embed module
    * @type {String}
    */
    this.version = null;
  }

  /**
  * Resolves options.embedURL to an embed and passes it to callback.
  * @param {Embed} embed - Embed object
  * @param {function(embed:Embed)} callback - callback to invoke after resolving embed
  * @throws {UnexpectedStatusError} throws when provider API returns a non-200 response.
  * @override
  */
  getEmbed (embed, callback) {
    this.makeAPIRequest(embed, function (embed) {
      callback(embed);
    });
  }

  /**
  * Makes the requests to the oembed provider's API
  * @param {Embed} embed - embed object
  * @param {function(embed:Ebmed)} callback - callback to invoke after resolving embed
  */
  makeAPIRequest (embed, callback) {
    let self = this;
    let requestOptions = self.buildRequestOptions(embed);
    let oembedAPIURL = self.buildAPIURL(embed, requestOptions);
    requestOptions.url = oembedAPIURL;

    self.request(requestOptions, function (error, response, body) {
      if (response && response.request && response.request.uri && response.request.uri.href) {
        embed.oembedAPIURL = response.request.uri.href;
      } else {
        embed.oembedAPIURL = oembedAPIURL;
      }

      if (!error && response.statusCode === 200) {
        self.parseResponseBody(body, function (error, data) {
          // json or xml parsing error
          if (error) {
            self._applyErrorToEmbed(error, embed);
            callback(embed);
            return;
          // successfuly parsed
          } else {
            embed.data = data;
            self.filterData(embed.data);
            callback(embed);
            return;
          }
        });
      } else {
        // Error making request
        if (error) {
          self._applyErrorToEmbed(error, embed);
          callback(embed);
          return;
        // No error, but non-200 HTTP response
        } else {
          let errorMarkupFunctionName = 'errorMarkup' + response.statusCode;
          let errorMarkupFunction = self[errorMarkupFunctionName] ? self[errorMarkupFunctionName] : self.errorMarkup;
          let error = new UnexpectedStatusError('HTTP status ' + response.statusCode + ' for embed provider URL: ' + oembedAPIURL);
          error.status = response.statusCode;
          self._applyErrorToEmbed(error, embed, errorMarkupFunction);
          callback(embed);
          return;
        }
      }
    });
  }

  /**
  * Puts the Embed into an error state if an error occurs
  * @param {Error} error - the Error that occurred
  * @param {Embed} embed - the Embed object
  * @param {function(embed: Embed, error: Error)} [errorMarkupFunction] - function that generates error markup
  */
  _applyErrorToEmbed (error, embed, errorMarkupFunction) {
    if (!errorMarkupFunction) {
      errorMarkupFunction = this.errorMarkup;
    }
    embed.data.html = errorMarkupFunction(embed, error);
    embed.error = error;
  }

  /**
  * Parses the provider API response into a data object
  * @param {String} body - API response
  * @param {function(error: Error, data: Object)} callback - callback to invoke with resulting oembed data
  */
  parseResponseBody (body, callback) {
    body = this.convertHighBitUnicodeToSurrogates(body);
    let data;
    if (this.format === 'json') {
      try {
        data = JSON.parse(body);
        callback(null, data);
      } catch (error) {
        callback(error);
      }
      return;
    } else {
      let xmlOptions = {
        explicitArray: false,
        valueProcessors: [
          xml2js.processors.parseNumbers,
          xml2js.processors.parseBooleans
        ]
      };
      data = xml2js.parseString(body, xmlOptions, function (error, result) {
        if (error) {
          callback(error);
        } else {
          data = result.oembed;
          callback(null, data);
        }
        return;
      });
    }
  }

  /**
  * Builds the API URL string
  * @param {Embed} embed - Embed object
  * @return {String} - API URL
  */
  buildAPIURL (embed) {
    let qs = {};
    qs.url = embed.embedURL;
    qs.format = this.format;
    if (embed.options.maxWidth) qs.maxwidth = embed.options.maxWidth;
    if (embed.options.maxHeight) qs.maxheight = embed.options.maxHeight;

    if (this.defaultProviderQueryStringParameters) {
      for (let name in this.defaultProviderQueryStringParameters) {
        qs[name] = this.defaultProviderQueryStringParameters[name];
      }
    }

    return this.providerURL + '?' + querystring.stringify(qs);
  }

  /**
  * Builds the HTTP request options (headers, etc.)
  * @see https://www.npmjs.com/package/request
  * @param {Embed} embed - Embed object
  * @return {Object} - request options
  */
  buildRequestOptions (embed) {
    if (!this.version) {
      let fileContents = fs.readFileSync(__dirname + '/../../package.json');
      let packageJSON = JSON.parse(fileContents);
      this.version = packageJSON.version;
    }

    let requestOptions = {
      headers: {
        'User-Agent': 'URLEmbed Module HTTP Agent ' + this.version
      },
      timeout: this.timeoutMs
    };
    return requestOptions;
  }

  /**
  * Converts high-bit-value escaped unicode code points to unicode surrogate pair code points.
  *
  * JSON.parse yields a parse error when it encounters unicode literals > 16-bits.
  * Notably, many newer emojis fall into this category.
  *
  * To solve for this you need to split the > 16-bit unicode escapedcode point into surrogate pairs.
  *
  * Great and entertaining explaination here:
  * {@link https://gist.github.com/mranney/1707371}
  *
  * Algorithm for calculating surrogate pairs cribbed from here:
  * {@link http://www.russellcottrell.com/greek/utilities/surrogatepaircalculator.htm}
  *
  * @param {String} body - The raw body of the HTTP response from the oembed provider's API
  * @return {String} - The transformed body with surrogate pairs
  */
  convertHighBitUnicodeToSurrogates (body) {
    return body.replace(/\\U([\da-f]{8})/gm, function (match, scalarVal) {
      let S = parseInt('0x' + scalarVal, 16);
      let H = Math.floor((S - 0x10000) / 0x400) + 0xD800;
      let L = ((S - 0x10000) % 0x400) + 0xDC00;
      return '\\u' + H.toString(16) + '\\u' + L.toString(16);
    });
  }

  /**
  * Configures the provider
  * @param {Object} configOptions
  * @param {number} configOptions.timeoutMs - Request timeout in milliseconds.
  * @override
  */
  configure (configOptions) {
    if (configOptions && configOptions.timeoutMs) {
      this.timeoutMs = configOptions.timeoutMs;
    }
  }
}

/**
* The data format of the provider's oembed service. Defaults to 'json'
* @identifier format
* @type {String}
*/
OEmbedProvider.prototype.format = 'json';

/**
* Default request timeout in milliseconds. Defaults to 2000.
* @type {number}
* @identifier timeoutMs
*/
OEmbedProvider.prototype.timeoutMs = 2000;

/**
* Reference to the request module.
* @type {Object}
* @see https://www.npmjs.com/package/request
*/
OEmbedProvider.prototype.request = request;

/**
* Object containing any any odd querystring parameters that the provider's oembed service requires.
* @type {Object}
*/
OEmbedProvider.prototype.defaultProviderQueryStringParameters = null;

module.exports = OEmbedProvider;

'use strict';

/**
* Converts a pattern of URLs into markup
* @interface
*/
class URLEmbedProvider {
  /**
  * @param {Array<RegExp>} urlPatterns - array of regular expressions that this provider will match
  */
  constructor (urlPatterns) {
    /**
    * Array of regular expressions that this provider will match
    * @type {Array<RegExp>}
    */
    if (urlPatterns) this.urlPatterns = urlPatterns;
  }

  /**
  * Determines if a given URL matches this provider
  * @param {String} embedURL - URL to embed
  * @return {boolean}
  */
  isMatch (embedURL) {
    let matches = false;
    for (let idx = 0; idx < this.urlPatterns.length; idx++) {
      if (embedURL.match(this.urlPatterns[idx])) {
        matches = true;
        idx = this.urlPatterns.length;
      }
    }
    return matches;
  }

  /**
  * Resolves options.embedURL to an embed and passes it to callback.
  * @param {Embed} embed - Embed object
  * @param {function(error: Error, data: Object)} callback - callback to invoke after resolving embed
  */
  getEmbed (embed, callback) {
    try {
      this.filterData(embed.data);
      callback(embed);
    } catch (error) {
      embed.error = error;
      embed.data = {
        html: this.errorMarkup(embed)
      };
      callback(embed);
    }
  }

  /**
  * Can be overridden/replaced to modify a specific provider's data before it is passed to the client callback.<br />
  * @param {{html: String}} data - Data object containing embed html
  */
  filterData (data) {}

  /**
  * Returns markup if an error occurs resolving the embed
  * @param {Embed} embed - Embed object
  */
  errorMarkup (embed) {
    return '<a href="' + embed.embedURL + '">' + embed.embedURL + '</a>';
  }

  /**
  * Stub method caled by EmbedEngine. Can be overridden.
  * @param {Object} engineOptions
  */
  configure (engineOptions) {

  }
}

module.exports = URLEmbedProvider;

